$(function(){module("bootstrap-alerts");test("should be defined on jquery object",function(){ok($(document.body).alert,"alert method is defined")});test("should return element",function(){ok($(document.body).alert()[0]==document.body,"document.body returned")});test("should fade element out on clicking .close",function(){var a=$('<div class="alert-message warning fade in"><a class="close" href="#" data-dismiss="alert">\u00d7</a><p><strong>Holy guacamole!</strong> Best check yo self, you\'re not looking too good.</p></div>').alert();
a.find(".close").click();ok(!a.hasClass("in"),"remove .in class on .close click")});test("should remove element when clicking .close",function(){$.support.transition=!1;var a=$('<div class="alert-message warning fade in"><a class="close" href="#" data-dismiss="alert">\u00d7</a><p><strong>Holy guacamole!</strong> Best check yo self, you\'re not looking too good.</p></div>').appendTo("#qunit-fixture").alert();ok($("#qunit-fixture").find(".alert-message").length,"element added to dom");a.find(".close").click();
ok(!$("#qunit-fixture").find(".alert-message").length,"element removed from dom")})});
